console.log('Je suis un module secondaire !');

//affichage du dossier dans lequel le module se situe.
console.log(__dirname);

//affichage du dossier dans lequel s'exécute le processus (programme exécuté) qui utilise le module
console.log(process.cwd());

//ma fonction
var bonjour = function(nombre){
  for(var i = 1; i <= nombre; i ++){
    global.console.log('Bonjour ' + i + ' fois !');
  };
}

//copie une référence à la fonction dans une propriété de module.exports
module.exports.fonctionBonjour = bonjour;

//déclaration de l'Array contenant 3 messages
var mesMessages = ['Bonjour', 'le', 'Monde !'];

//copie une référence à cet Array dans une propriété de module.exports
module.exports.lesMessages = mesMessages;

//déclaration de l'objet contenant un texte et une méthode utilisant ce texte
var monObjet = {
  texte:'Hello world !',
  afficheTexte: function(){
    console.log(this.texte);
  }
};

//copie une référence à cet objet dans une propriété de module.exports
module.exports.lObjet = monObjet;