const monModuleFileSystem = require('fs');

var afficheFichierDansConsole = function(fichier){

  monModuleFileSystem.access(fichier, monModuleFileSystem.R_OK, function(error){
    //Callback de .access()
    if(error){
      console.log('Impossible de lire le fichier ' + fichier + '.');
    } else {
      monModuleFileSystem.readFile(fichier,{encoding:'utf8'}, function(error, data){
        //Callback de .readFile()
        if(error){
          console.log('Impossible de lire le fichier ' + fichier + '.');
        } else {
          console.log(data);
        }
      });
    };
  });

};

module.exports.afficheFichier = afficheFichierDansConsole;