const monModuleDNS = require('dns');

var estUnNomDeDomaineValide = function(nom){

  monModuleDNS.lookup(nom, function(error, addresses, family){
    if(addresses){
      console.log('Le mot ' + nom + ' à pour adresse IPv4 :');
      console.log(addresses);
    }else{
      console.log('Ce mot ne correspond pas à une adresse IPv4');
    };
  });

};

module.exports.estUnNomDeDomaine = estUnNomDeDomaineValide;